    /**
     * Creates a new document within an existing case. An ID for the document is
     * not required in the request.
     *
     * @param document document data
     * @response 401 User not authorized
     * @response 403 User not authenticated
     */
    @Override
    @PUT
    @Produces(MediaType.APPLICATION_JSON)
    @Consumes(MediaType.APPLICATION_JSON)
    @Path("/document/create")
    @RolesAllowed({"writeArchiveFileRole"})
    public Response createDocument(RestfulDocumentContentV1 document) {

        // curl -X PUT "http://localhost:8080/j-lawyer-io/rest/cases/document/create" -H  "accept: application/json" -H  "Content-Type: application/json" -d "{  \"base64content\": \"\",  \"caseId\": \"0c79112f7f000101327bf357f0b6010c\",  \"fileName\": \"schnuffel.jpg\",  \"id\": \"\"}"
        try {

            InitialContext ic = new InitialContext();
            ArchiveFileServiceLocal cases = (ArchiveFileServiceLocal) ic.lookup("java:global/j-lawyer-server/j-lawyer-server-ejb/ArchiveFileService!com.jdimension.jlawyer.services.ArchiveFileServiceLocal");

            byte[] data = new Base64().decode(document.getBase64content());

            ArchiveFileDocumentsBean newDoc = cases.addDocument(document.getCaseId(), document.getFileName(), data, "");

            RestfulDocumentContentV1 doc = new RestfulDocumentContentV1();
            doc.setCaseId(document.getCaseId());
            doc.setFileName(newDoc.getName());
            doc.setId(newDoc.getId());
            if(newDoc.getFolder()!=null)
                doc.setFolderId(newDoc.getFolder().getId());

            Response res = Response.ok(doc).build();
            return res;
        } catch (Exception ex) {
            log.error("can not create new document " + document.getFileName(), ex);
            Response res = Response.serverError().build();
            return res;
        }
    }

    /**
     * Updates a document. This method can be used for both renaming a document
     * as well as for uploading new content. For renaming, the request does not
     * need to provide content. For uploading new content, there is no need to
     * provide a file name.
     *
     * @param document the documents new data
     * @response 401 User not authorized
     * @response 403 User not authenticated
     */
    @Override
    @PUT
    @Produces(MediaType.APPLICATION_JSON)
    @Consumes(MediaType.APPLICATION_JSON)
    @Path("/document/update")
    @RolesAllowed({"writeArchiveFileRole"})
    public Response updateDocument(RestfulDocumentContentV1 document) {

        // curl -X PUT "http://localhost:8080/j-lawyer-io/rest/cases/document/update" -H  "accept: application/json" -H  "Content-Type: application/json" -d "{  \"base64content\": \"\",  \"caseId\": \"string\",  \"fileName\": \"schnuffelschnaffel.jpg\",  \"id\": \"a0d1d7487f0001016dd877cd9684ba8e\"}"
        try {

            InitialContext ic = new InitialContext();
            ArchiveFileServiceLocal cases = (ArchiveFileServiceLocal) ic.lookup("java:global/j-lawyer-server/j-lawyer-server-ejb/ArchiveFileService!com.jdimension.jlawyer.services.ArchiveFileServiceLocal");

            ArchiveFileDocumentsBean currentDoc = cases.getDocument(document.getId());
            if (currentDoc == null) {
                log.error("document with id " + document.getId() + " does not exist");
                Response res = Response.serverError().build();
                return res;
            }

            if (!currentDoc.getName().equals(document.getFileName())) {
                cases.renameDocument(document.getId(), document.getFileName());
            }
            
            if (document.getBase64content() != null && !"".equals(document.getBase64content())) {
                byte[] newContent = new Base64().decode(document.getBase64content());
                cases.setDocumentContent(document.getId(), newContent);
            }

            if(document.getFolderId()!=null) {
                ArrayList<String> docIds=new ArrayList<>();
                docIds.add(document.getId());
                cases.moveDocumentsToFolder(docIds, document.getFolderId());
            }
            
            ArchiveFileDocumentsBean updated = cases.getDocument(document.getId());
            
            RestfulDocumentContentV1 doc = new RestfulDocumentContentV1();
            doc.setCaseId(document.getCaseId());
            doc.setFileName(updated.getName());
            doc.setId(updated.getId());
            if(updated.getFolder()!=null)
                doc.setFolderId(updated.getFolder().getId());

            Response res = Response.ok(doc).build();

            return res;
        } catch (Exception ex) {
            log.error("can not create new document " + document.getFileName(), ex);
            Response res = Response.serverError().build();
            return res;
        }

    }
    
    

